import axios from "axios";
import { TOKEN_CYBERSOFT } from "../constants/common";

const fetchMovieListApi = () => {
    return axios({
        url: 'https://movienew.cybersoft.edu.vn/api/QuanLyPhim/LayDanhSachPhim?maNhom=GP03',
        method: 'GET',
        headers: {
            TokenCybersoft: TOKEN_CYBERSOFT,

        },
    });
};

const fetchMovieDetailApi = () => {
    return axios({
        url: 'https://movienew.cybersoft.edu.vn/api/QuanLyPhim/LayThongTinPhim?MaPhim=0',
        method: 'GET',
        headers: {
            TokenCybersoft: TOKEN_CYBERSOFT
        }
    });
};

export { fetchMovieListApi, };
