import "./index.scss";
import React, { useState } from 'react';
import { fetchMovieScheduleApi } from "../../services/cinema";
import { useParams } from "react-router-dom";
import { useEffect } from "react";

export default function Schedule() {
    const [schedule, setSchedule] = useState({});

    const params = useParams();
    useEffect(() => {
        fetchMovieSchedule();
    });
    const fetchMovieSchedule = async () => {
        const result = await fetchMovieScheduleApi(params.movieId);
        setSchedule(result.data.content);
    };

    const renderTab = () => {
        return schedule.heThongRapChieu.map((ele) => {
            return (
                <a
                    key={ele.maHeThongRap}
                    className="nav-link cinema__nav active"
                    data-toggle="pill"
                    href="#brand-1"
                >
                    {ele.tenHeThong}
                </a>
            );
        });
    };

    return (
        <section className="py-5 schedule">
            <div className="container">
                <h1 className="text-center">Schedule</h1>
                <div className="row">
                    {/* Nav pills */}
                    <div className="col-lg-4">
                        <ul className="nav nav-pills cinema__pills flex-column " role="tablist">
                            <li className="nav-item">
                                <a className="nav-link cinema__nav active" data-toggle="pill" href="#brand-1">
                                    <img className="rounded m-auto cinema__logo" src="./images/cgv.png" alt="CGV" />
                                </a>
                            </li>
                            <li className="nav-item">
                                <a className="nav-link cinema__nav" data-toggle="pill" href="#brand-3">
                                    <img className="rounded m-auto cinema__logo" src="./images/cinestar.png" alt="Cinestar" />
                                </a>
                            </li>
                            <li className="nav-item">
                                <a className="nav-link cinema__nav" data-toggle="pill" href="#brand-2">
                                    <img className="rounded m-auto cinema__logo" src="./images/lotte.png" alt="Lotte" />
                                </a>
                            </li>
                        </ul>
                    </div>
                    {/* Tab panes */}
                    <div className="col-lg-8">
                        <div className="tab-content">
                            <div id="brand-1" className="container m-auto tab-pane active"><br />
                                <h3>CGV</h3>
                                <p>Lorem ipsum dolor sit amet, consectetur adipisicing elit, sed do eiusmod tempor
                                    incididunt ut
                                    labore et dolore magna aliqua.</p>
                            </div>
                            <div id="brand-3" className="container m-auto tab-pane fade"><br />
                                <h3>Cinestar</h3>
                                <p>Sed ut perspiciatis unde omnis iste natus error sit voluptatem accusantium doloremque
                                    laudantium, totam rem aperiam.</p>
                            </div>
                            <div id="brand-2" className="container m-auto tab-pane fade"><br />
                                <h3>Lotte</h3>
                                <p>Ut enim ad minim veniam, quis nostrud exercitation ullamco laboris nisi ut aliquip ex
                                    ea
                                    commodo consequat.</p>
                            </div>
                        </div>
                    </div>
                </div>
            </div>
        </section>
    );
};
