import "./index.scss";
import React, { useState } from 'react';
import { fetchMovieScheduleApi } from "../../services/cinema";
import { useParams } from "react-router-dom";
import { useEffect } from "react";

export default function Schedule() {
    const [schedule, setSchedule] = useState({});

    const params = useParams();
    useEffect(() => {
        fetchMovieSchedule();
    });
    const fetchMovieSchedule = async () => {
        const result = await fetchMovieScheduleApi(params.movieId);
        console.log(result);
        setSchedule(result.data.content);
    };

    const renderTab = () => {
        return schedule?.heThongRapChieu?.map((ele, idx) => {
            return (
                <a
                    key={ele.maHeThongRap}
                    className={`nav-link cinema__nav ${idx = 0 && 'active'} `}
                    data-toggle="pill"
                    href={`#${ele.maHeThongRap}`}
                >
                    {ele.tenHeThongRap}
                </a>
            );
        });
    };

    const renderContents = () => {
        return schedule?.heThongRapChieu?.map((ele, idx) => {
            return (
                <div
                    key={ele.maHeThongRap}
                    id={ele.maHeThongRap}
                    className={`container m-auto tab-pane fade ${idx = 0 && 'active'}`}
                >
                    {
                        ele.cumRapChieu.map((ele) => {
                            return (
                                <div
                                    key={ele.maCumRap}
                                    className="row mb-5">
                                    <div className="col-1">
                                        <img className="img-fluid rounded"
                                            src="https://s3img.vcdn.vn/123phim/2021/01/bhd-star-bitexco-16105952137769.png" />
                                    </div>
                                    <div className="col-11 pl-0">
                                        <h5>Galaxy Cinema Cineplex - 3/2</h5>
                                        <span className="text-muted">L5-Vincom 3/2, 3C Đường 3/2, Q.10</span>
                                    </div>
                                    <div className="col-12">
                                        <div className="row">
                                            <div className="col-3">
                                                <a href>2022-12-12T09:30:00</a>
                                            </div>
                                            <div className="col-3">
                                                <a href>2022-12-12T09:30:00</a>
                                            </div>
                                            <div className="col-3">
                                                <a href>2022-12-12T09:30:00</a>
                                            </div>
                                            <div className="col-3">
                                                <a href>2022-12-12T09:30:00</a>
                                            </div>
                                        </div>
                                    </div>
                                </div>

                            );
                        })}
                </div>);
        });
    };
    return (
        <section className="py-5 schedule">
            <div className="container">
                <h1 className="text-center">Schedule</h1>
                <div className="row">
                    {/* Nav pills */}
                    <div className="col-lg-4">
                        <ul className="nav nav-pills cinema__pills flex-column " role="tablist">
                            <li className="nav-item">
                                {renderTab()}
                            </li>

                        </ul>
                    </div>
                    {/* Tab panes */}
                    <div className="col-lg-8">
                        <div className="tab-content">
                            {renderContents()}
                        </div>
                    </div>
                </div>
            </div>
        </section>
    );
};
